mapper.insert:
INSERT INTO CAMERA_NUM ( CAMERA_ID,REGION_ID,INDEX_CODE,NAME,CAMERA_TYPE,NUM,TIME ) select A.* FROM (
select #{cn.cameraId,jdbcType=INTEGER},#{cn.regionId,jdbcType=INTEGER},#{cn.indexCode,jdbcType=VARCHAR}, #{cn.name,jdbcType=VARCHAR},#{cn.cameraType,jdbcType=INTEGER},#{cn.num,jdbcType=INTEGER},#{cn.time,jdbcType=DATE} from dual
)A

mapper.select:
SELECT
*
FROM
(
SELECT
tt.*,
ROWNUM AS rowno
FROM
( SELECT INDEX_CODE,NAME,CAMERA_TYPE,SUM(NUM) NUM FROM CAMERA_NUM
WHERE 1 = 1

    and REGION_ID=#{cameraInfo.regionId}


    and CITYNO=#{cameraInfo.cityNo}


    and TIME between #{beginDateScope[0]} and #{beginDateScope[1]}

GROUP BY INDEX_CODE,NAME,CAMERA_TYPE
order by NUM desc) tt
WHERE

    ROWNUM <= #{size}

) table_alias WHERE

    table_alias.rowno >= #{page}
